#pragma mark - lengthOfLongestSubstring

int continueMax(string s, int startIndex)
{
    int maxLength = 0;
    
    if( startIndex >= 0 && startIndex < s.length() )
    {
        unordered_set<char> set;
        for( int i = startIndex; i < s.length(); i++ )
        {
            if( set.find(s[i]) == set.end() )
            {
                maxLength++;
                set.insert(s[i]);
            }
            else
            {
                return maxLength;
            }
        }
    }
    
    return maxLength;
}

bool isUniqueString(string s, int start, int length)
{
    bool unique = true;
    unordered_set<char> set;
    string substring = s.substr(start, length);
    for( int i = 0; i < substring.length(); i++ )
    {
        if( set.find( substring[i]) == set.end() )
        {
            set.insert(substring[i]);
        }
        else
        {
            unique = false;
            break;
        }
    }
    
    return unique;
}

int lengthOfLongestSubstring(string s)
{
    int maxLength = 0;
    int lastRepeatCharIndex = 0;
    
    unordered_set<char> uniqueSubStrings;
    for( int i = 0; i < s.length(); i++ )
    {
        char c = s[i];
        if(uniqueSubStrings.find(c) == uniqueSubStrings.end())
        {
            // Not in the maps, then it's a unique to current substring.
            uniqueSubStrings.insert(c);
        }
        else
        {
            // Found repeat
            for( int j = lastRepeatCharIndex; j < i; j++ )
            {
                if( s[j] == s[i] )
                {
                    lastRepeatCharIndex = j + 1;
                    break;
                }

                uniqueSubStrings.erase(s[j]);
            }
        }
        
        maxLength = max( maxLength, (int)uniqueSubStrings.size() );
    }
    
    return maxLength;
}

