#pragma mark - ZigZag Conversion

string convert(string s, int numRows) {
    enum State
    {
        Down,
        RT // Right Top
    };
    
    if( numRows < 2 )
        return s;
    
    vector<vector<int>> zigzag;
    
    State state = Down;
    int currentCol = 0;
    int zigRow = 0;
    for( int i = 0; i < s.length(); i++ )
    {
        if( state == Down )
        {
            if( zigzag.size() == currentCol )
            {
                zigzag.push_back( vector<int>(numRows, -1) );
            }
            
            vector<int>& col = zigzag[currentCol];
            if( zigRow < numRows )
            {
                col[zigRow] = s[i];
                zigRow++;
            }
            else
            {
                // Full change to next state.
                state = RT;
                zigRow = numRows - 2;
                i--;
                currentCol++;
            }
        }
        else
        {
            if( zigRow > 0 )
            {
                if( zigzag.size() == currentCol)
                {
                    zigzag.push_back( vector<int>(numRows, -1) );
                }
                
                vector<int>& col = zigzag[currentCol];
                col[zigRow] = s[i];
                zigRow--;
                currentCol++;
            }
            else
            {
                state = Down;
                i--;
                zigRow = 0;
            }
        }
    }
    
    string result;
    
    for( int i = 0; i < numRows; i++ )
    {
        for( int j = 0; j < zigzag.size(); j++ )
        {
            vector<int>& col = zigzag[j];

//             cout << (col[i] > 0 ? (char)col[i] : ' ') << "  ";
            if( col[i] != -1 )
                result.push_back((char)col[i]);
        }
        
//        cout << endl;
    }
    
    return result;
}

