#pragma mark - twoSum

vector<int> Level1::twoSum_binarysearch(vector<int>& nums, int target)
{
    vector<int> v;
    
    vector<int> sortedNums = nums;
    sort(sortedNums.begin(), sortedNums.end());
    
    for( int i = 0; i < nums.size(); i++ )
    {
        int lookingFor = target - nums[i];
        if( binary_search( sortedNums.begin(), sortedNums.end(), lookingFor ) )
        {
            for( int j = i + 1; j < nums.size(); j++ )
            {
                if( nums[j] == lookingFor )
                {
                    v.push_back( i + 1 );
                    v.push_back( j + 1 );
                    
                    return v;
                }
            }
        }
    }
    
    return v;
}

vector<int> Level1::twoSum(vector<int>& nums, int target)
{
    vector<int> v;
    
    map<int, int > map;
    
    for( int i = 0; i < nums.size(); i++ )
    {
        int lookingFor = target - nums[i];
        if( map.count( lookingFor ) ==  1 )
        {
            v.push_back( map[lookingFor] + 1);
            v.push_back( i + 1 );
            
            return v;
        }
        
        map[nums[i]] = i;
    }
    
    return v;
}

void Level1::runTwoSum()
{
    vector<int> v{3, 2, 4};
    
    for( auto i : twoSum(v, 6) )
    {
        cout << i << endl;
    }
}

#pragma mark - addTwoNumbers

struct ListNode {
    int val;
    ListNode *next;
    ListNode(int x) : val(x), next(NULL) {}
};


ListNode* addTwoNumbers(ListNode* l1, ListNode* l2)
{
    ListNode* result = new ListNode(0); // dummy head
    ListNode* rit = result;  // result iterator
    
    ListNode* it1 = l1;
    ListNode* it2 = l2;
    
    // Find which list is longer.
    while( it1 != nullptr && it2 != nullptr )
    {
        it1 = it1->next;
        it2 = it2->next;
    }
    
    if( it1 == nullptr )
    {
        it1 = l2;
        it2 = l1;
    }
    else
    {
        it1 = l1;
        it2 = l2;
    }
    
    int addOne = 0;
    
    while( it1 != nullptr )
    {
        if( it2 != nullptr )
        {
            int sum = it1->val + it2->val + addOne;
            ListNode* newNode = new ListNode(sum >= 10 ? sum - 10 : sum );
            if( sum >= 10 )
                addOne = 1;
            else
                addOne = 0;

            rit->next = newNode;
            rit = rit->next;
            
            it1 = it1->next;
            it2 = it2->next;
        }
        else
        {
            int sum = it1->val + addOne;
            ListNode* newNode = new ListNode(sum >= 10 ? sum - 10 : sum );
            if( sum >= 10 )
                addOne = 1;
            else
                addOne = 0;
            
            rit->next = newNode;
            rit = rit->next;
            
            it1 = it1->next;
        }
    }
    
    if( addOne == 1)
    {
        rit->next = new ListNode(1);
    }
    
    return result->next;
}

ListNode* addTwoNumbers1(ListNode* l1, ListNode* l2)
{
    ListNode* result = new ListNode(0); // dummy head
    ListNode* rit = result;  // result iterator
    
    ListNode* it1 = l1;
    ListNode* it2 = l2;
    
    int addOne = 0;
    
    while( it1 != nullptr || it2 != nullptr )
    {
        if( it1 != nullptr && it2 != nullptr )
        {
            int sum = it1->val + it2->val + addOne;
            ListNode* newNode = new ListNode(sum >= 10 ? sum - 10 : sum );
            if( sum >= 10 )
                addOne = 1;
            else
                addOne = 0;
            
            rit->next = newNode;
            rit = rit->next;
            
            it1 = it1->next;
            it2 = it2->next;
        }
        else
        {
            // either one of them is nullptr
            int sum = (it1 != nullptr ? it1 : it2)->val + addOne;
            ListNode* newNode = new ListNode(sum >= 10 ? sum - 10 : sum );
            if( sum >= 10 )
                addOne = 1;
            else
                addOne = 0;
            
            rit->next = newNode;
            rit = rit->next;
            
            if( it1 != nullptr )
            {
                it1 = it1->next;
            }
            else
            {
                it2 = it2->next;
            }
        }
    }
    
    // This step is easy to miss. Notice it!!
    if( addOne == 1)
    {
        rit->next = new ListNode(1);
    }
    
    return result->next;
}

