#pragma mark - reverse integer

int reverse(int x)
{
    long r = 0;
    while( x != 0 )
    {
        r *= 10;
        r += x % 10;
        x = x / 10;
    }
    
    // This is important as reverse might cause the integer overflow.
    if( r > numeric_limits<int>::max() || r < numeric_limits<int>::min() )
        return 0;

    return static_cast<int>( r );
}

