#pragma mark - palindrome

bool isPalindromeBrute(int x)
{
    if( x < 0 )
    {
        return false;
    }
    else if( x < 10 )
    {
        return true;
    }
    else if( x < 100 )
    {
        return x / 10 == x % 10;
    }
    else
    {
        // bigger than 3 digits.
        string s = to_string(x);
        for( int i = 0; i < s.length() / 2; i++ )
        {
            if( s[i] != s[s.length() - i - 1] )
                return false;
        }
        
        return true;
    }
}

bool isPalindrome(int x)
{
    if( x < 0 )
    {
        return false;
    }
    else if( x < 10 )
    {
        return true;
    }
    else if( x < 100 )
    {
        return x / 10 == x % 10;
    }
    else
    {
        // bigger than 3 digits.
        long r = 0;
        int tmp = x;
        while( tmp != 0 )
        {
            r *= 10;
            r += tmp % 10;
            tmp = tmp / 10;
        }
        
        return r == x;
    }
}

